import{_ as t,c as s,o as r,ag as i}from"./chunks/framework.-ewJmwvT.js";const h=JSON.parse('{"title":"CSS属性权重","description":"","frontmatter":{},"headers":[],"relativePath":"前端技术/02 CSS/02 CSS进阶/高级机制/CSS属性权重.md","filePath":"前端技术/02 CSS/02 CSS进阶/高级机制/CSS属性权重.md"}'),l={name:"前端技术/02 CSS/02 CSS进阶/高级机制/CSS属性权重.md"};function o(e,a,n,c,S,_){return r(),s("div",null,a[0]||(a[0]=[i('<h1 id="css属性权重" tabindex="-1">CSS属性权重 <a class="header-anchor" href="#css属性权重" aria-label="Permalink to &quot;CSS属性权重&quot;">​</a></h1><h2 id="权重规则总结" tabindex="-1">权重规则总结: <a class="header-anchor" href="#权重规则总结" aria-label="Permalink to &quot;权重规则总结:&quot;">​</a></h2><ol><li>!important 优先级最高，但也会被权重高的important所覆盖</li><li>行内样式总会覆盖外部样式表的任何样式(除了!important)</li><li>单独使用一个选择器的时候，不能跨等级使css规则生效</li><li>如果两个权重不同的选择器作用在同一元素上，权重值高的css规则生效</li><li>如果两个相同权重的选择器作用在同一元素上：以后面出现的选择器为最后规则</li><li>权重相同时，与元素距离近的选择器生效</li></ol><h2 id="什么是-css-权重优先级" tabindex="-1">什么是 css 权重优先级？ <a class="header-anchor" href="#什么是-css-权重优先级" aria-label="Permalink to &quot;什么是 css 权重优先级？&quot;">​</a></h2><p>在同一个元素使用不同的方式，声明了相同的一条或多条css规则，<strong>浏览器会通过权重来判断哪一种方式的声明，与这个元素最为相关，从而在该元素上应用这个声明方式声明的所有css规则</strong>。</p><h2 id="权重的五个等级" tabindex="-1">权重的五个等级 <a class="header-anchor" href="#权重的五个等级" aria-label="Permalink to &quot;权重的五个等级&quot;">​</a></h2><ul><li>!important ，权重: 无穷大</li><li>行内样式，权重: 1000</li><li>ID选择器, 权重:100</li><li>class,属性选择器和伪类选择器，权重:10</li><li>标签选择器和伪元素选择器，权重:1</li></ul><p><strong>权重记忆口诀</strong>：<br> 从0开始，一个<strong>行内样式+1000</strong>，一个<strong>id+100</strong>，一个<strong>属性选择器/class或者伪类+10</strong>，一个<strong>元素名，或者伪元素+1</strong></p>',8)]))}const p=t(l,[["render",o]]);export{h as __pageData,p as default};
