import{_ as e,c as t,o as s,ae as r}from"./chunks/framework.CIivgcbp.js";const p="/obsidian-vitepress-docs/assets/05%20%E5%90%91%E9%87%8F%E5%9F%BA%E7%A1%80-2.DYWdVw4i.png",i="/obsidian-vitepress-docs/assets/05%20%E5%90%91%E9%87%8F%E5%9F%BA%E7%A1%80-1.D1B3C9cL.png",o="/obsidian-vitepress-docs/assets/05%20%E5%90%91%E9%87%8F%E5%9F%BA%E7%A1%80-3.KqTOpID0.png",A=JSON.parse('{"title":"05 向量基础","description":"","frontmatter":{},"headers":[],"relativePath":"前端技术/03 JavaScript/07浏览器交互/WebGL入门/05 向量基础.md","filePath":"前端技术/03 JavaScript/07浏览器交互/WebGL入门/05 向量基础.md"}'),n={name:"前端技术/03 JavaScript/07浏览器交互/WebGL入门/05 向量基础.md"};function _(c,a,d,l,h,m){return s(),t("div",null,a[0]||(a[0]=[r('<h1 id="_05-向量基础" tabindex="-1">05 向量基础 <a class="header-anchor" href="#_05-向量基础" aria-label="Permalink to &quot;05 向量基础&quot;">​</a></h1><h2 id="向量的概念​" tabindex="-1">向量的概念​ <a class="header-anchor" href="#向量的概念​" aria-label="Permalink to &quot;向量的概念​&quot;">​</a></h2><p>向量是一个具有方向和长度的量，它可以表示为一条线段的端点。向量具有两个主要的属性：大小和方向。大小表示向量从起点到终点的距离，方向表示从起点到终点的方向。​</p><p>从起点 A 指向终点 B，就是一个向量 AB。​</p><p><img src="'+p+'" alt=""></p><h2 id="向量的表示​" tabindex="-1">向量的表示​ <a class="header-anchor" href="#向量的表示​" aria-label="Permalink to &quot;向量的表示​&quot;">​</a></h2><p>向量可以用一个有序数对来表示，这个有序数对就是向量的坐标。在二维空间中，一个向量可以用两个数表示，即 x 坐标和 y 坐标。在三维空间中，一个向量需要三个数来表示，即 x 坐标、y 坐标和 z 坐标。​</p><p>对于二维平面中的向量，我们可以使用 (x，y) 来表示。其中，x 表示横坐标，y 表示纵坐标。例如，向量 (3，4) 表示一个起点为 (0，0)，终点为 (3，4) 的线段。​</p><p><img src="'+i+'" alt=""></p><p>对于三维空间中的向量，我们可以使用 (x，y，z) 来表示。其中，x 表示横坐标，y 表示纵坐标，z 表示竖坐标。例如，向量 (1，2，3) 表示一个起点为 (0，0，0)，终点为 (1，2，3) 的线段。</p><h2 id="向量的计算​" tabindex="-1">向量的计算​ <a class="header-anchor" href="#向量的计算​" aria-label="Permalink to &quot;向量的计算​&quot;">​</a></h2><p>还是以上图二维为例，OA 向量可以认为是 OB 向量 +OC 向量。OB 向量的坐标 (0,3)，OC 向量的坐标 (4,0)​</p><p>向量 OB + 向量 OC = (0,3) + (4,0) = (3,4)​</p><p>如果此时，有一条向量 AD 向量，那么 O 到 D 也是一条向量 OD 向量。​</p><p><img src="'+o+'" alt=""></p><p>​<br> 向量 OD = 向量 OA + 向量 AD = (3,4) + (3,1) = (6,5)</p>',16)]))}const E=e(n,[["render",_]]);export{A as __pageData,E as default};
