import{_ as a,c as e,o as r,ae as o}from"./chunks/framework.CIivgcbp.js";const s="/obsidian-vitepress-docs/assets/image_J-BM2mpL7S.aBM0_4Hj.png",h=JSON.parse('{"title":"URL与DNS","description":"","frontmatter":{},"headers":[],"relativePath":"计算机基础课程/网络/01 网络原理/03_URL与DNS.md","filePath":"计算机基础课程/网络/01 网络原理/03_URL与DNS.md"}'),n={name:"计算机基础课程/网络/01 网络原理/03_URL与DNS.md"};function i(l,t,p,_,c,d){return r(),e("div",null,t[0]||(t[0]=[o('<h1 id="url与dns" tabindex="-1">URL与DNS <a class="header-anchor" href="#url与dns" aria-label="Permalink to &quot;URL与DNS&quot;">​</a></h1><h2 id="url" tabindex="-1">URL <a class="header-anchor" href="#url" aria-label="Permalink to &quot;URL&quot;">​</a></h2><p>URL全称<strong>Uniform Resource Locator</strong>，<strong>统一资源定位器</strong>，用于网络地址的定位。</p><p>URL由三部分组成：资源类型、存放资源的主机域名、资源文件名。也可认为由4部分组成：<strong>协议、主机、端口、路径</strong><br><img src="'+s+'" alt="D-学习笔记/计算机基础课程/网络/01 网络原理/image/imageJ-BM2mpL7S.png"><br> URL的一般语法格式为：</p><p><code>protocol :// hostname[:port] / path / [:parameters][?query]#fragment</code></p><p>其中带方括号的为可选项</p><p>protocol常用的有http、https、ftp等</p><h2 id="dns" tabindex="-1">DNS <a class="header-anchor" href="#dns" aria-label="Permalink to &quot;DNS&quot;">​</a></h2><p>DNS全称<strong>Domain Name Syste</strong>m，<strong>域名系统</strong>，是互联网的一项服务。它作为将域名和IP地址相互映射的一个分布式数据库，能够使人更方便地访问互联网。</p><p>DNS使用的是UDP，端口53。当前，对于每一级域名长度的限制是63个字符，域名总长度则不能超过253个字符。</p><p>DNS查询顺序：</p><ul><li>浏览器缓存DNS</li><li>操作系统缓存DNS</li><li>本地hosts文件</li><li>向域名服务器发送请求 <ul><li>根域名服务器→顶级域名服务器→权威域名服务器</li></ul></li></ul>',12)]))}const S=a(n,[["render",i]]);export{h as __pageData,S as default};
