import{_ as t,c as a,o as r,ag as i}from"./chunks/framework.-ewJmwvT.js";const o="/obsidian-vitepress-docs/assets/11_%E5%89%8D%E7%AB%AF%E9%89%B4%E6%9D%83-%E5%8D%95%E7%82%B9%E7%99%BB%E5%BD%95-2.DVHJH-2y.png",b=JSON.parse('{"title":"前端鉴权 - 单点登录","description":"","frontmatter":{},"headers":[],"relativePath":"计算机基础课程/网络/01 网络原理/11_前端鉴权-单点登录.md","filePath":"计算机基础课程/网络/01 网络原理/11_前端鉴权-单点登录.md"}'),n={name:"计算机基础课程/网络/01 网络原理/11_前端鉴权-单点登录.md"};function l(s,e,h,c,_,u){return r(),a("div",null,e[0]||(e[0]=[i('<h1 id="前端鉴权-单点登录" tabindex="-1">前端鉴权 - 单点登录 <a class="header-anchor" href="#前端鉴权-单点登录" aria-label="Permalink to &quot;前端鉴权 - 单点登录&quot;">​</a></h1><h2 id="什么是认证-authentication" tabindex="-1">什么是认证（Authentication） <a class="header-anchor" href="#什么是认证-authentication" aria-label="Permalink to &quot;什么是认证（Authentication）&quot;">​</a></h2><ul><li>通俗地讲就是<strong>验证当前用户的身份</strong>，证明“你是你自己”（比如：你每天上下班打卡，都需要通过指纹打卡，当你的指纹和系统里录入的指纹相匹配时，就打卡成功）</li><li>互联网中的认证：<br> - 用户名密码登录<br> - 邮箱发送登录链接<br> - 手机号接收验证码<br> - 只要你能收到邮箱/验证码，就默认你是账号的主人</li></ul><h2 id="什么是授权-authorization" tabindex="-1">什么是授权（Authorization） <a class="header-anchor" href="#什么是授权-authorization" aria-label="Permalink to &quot;什么是授权（Authorization）&quot;">​</a></h2><ul><li><strong>用户授予第三方应用访问该用户某些资源的权限</strong><br> - 你在安装手机应用的时候，APP 会询问是否允许授予权限（访问相册、地理位置等权限）<br> - 你在访问微信小程序时，当登录时，小程序会询问是否允许授予权限（获取昵称、头像、地区、性别等个人信息）</li><li>实现授权的方式有：cookie、session、token、OAuth</li></ul><h2 id="什么是凭证-credentials" tabindex="-1">什么是凭证（Credentials） <a class="header-anchor" href="#什么是凭证-credentials" aria-label="Permalink to &quot;什么是凭证（Credentials）&quot;">​</a></h2><ul><li><strong>实现认证和授权的前提</strong>是需要一种<strong>媒介（证书）</strong> 来标记访问者的身份<br> - 在战国时期，商鞅变法，发明了照身帖。照身帖由官府发放，是一块打磨光滑细密的竹板，上面刻有持有人的头像和籍贯信息。国人必须持有，如若没有就被认为是黑户，或者间谍之类的。<br> - 在现实生活中，每个人都会有一张专属的 <a href="https://link.juejin.cn?target=https://baike.baidu.com/item/%E5%B1%85%E6%B0%91%E8%BA%AB%E4%BB%BD%E8%AF%81/2080960" title="居民身份证" target="_blank" rel="noreferrer">居民身份证</a>，是用于证明持有人身份的一种法定证件。通过身份证，我们可以办理手机卡/银行卡/个人贷款/交通出行等等，这就是<strong>认证的凭证。</strong><br> - 在互联网应用中，一般网站（如掘金）会有两种模式，游客模式和登录模式。游客模式下，可以正常浏览网站上面的文章，一旦想要点赞/收藏/分享文章，就需要登录或者注册账号。当用户登录成功后，服务器会给该用户使用的浏览器颁发一个令牌（token），这个令牌用来表明你的身份，每次浏览器发送请求时会带上这个令牌，就可以使用游客模式下无法使用的功能。</li></ul><blockquote><p>参考:<br><a href="https://juejin.cn/post/6844904034181070861?searchId=20231007150038F468CC966E5AD04A0297" target="_blank" rel="noreferrer">傻傻分不清之 Cookie、Session、Token、JWT在战国时期，商鞅变法，发明了照身帖。照身帖由官府发放，是一 - 掘金</a><br><a href="https://juejin.cn/post/6898630134530752520?searchId=20231007150038F468CC966E5AD04A0297" target="_blank" rel="noreferrer">前端鉴权的兄弟们：cookie、session、token、jwt、单点登录我们知道，HTTP 是无状态的。也就是说，H - 掘金</a></p></blockquote><h2 id="单点登录" tabindex="-1">单点登录 <a class="header-anchor" href="#单点登录" aria-label="Permalink to &quot;单点登录&quot;">​</a></h2><ul><li>单点登录英文全称==Single Sign On==，简称 SSO。</li><li>指在多系统应用群中登录一个系统，便可在其他所有系统中得到授权而无需再次登录，包括单点登录与单点注销两部分。</li><li>实现方案： <ol><li>Seesion +Cookie ：需要单独的认证中心，服务器成本高</li><li>Token</li><li>RefreshToken + Token</li></ol></li></ul><p><img src="'+o+'" alt=""></p>',11)]))}const k=t(n,[["render",l]]);export{b as __pageData,k as default};
