import{_ as e}from"./chunks/EasyFishingMod.readme-1.DapE9Rj-.js";import{_ as r,c as a,o as n,ae as t}from"./chunks/framework.CIivgcbp.js";const i="/obsidian-vitepress-docs/assets/ModSettingManagerForDolocTown.readme-2.CZYv3Mct.png",f=JSON.parse('{"title":"ModSettingManagerForDolocTown.readme","description":"","frontmatter":{},"headers":[],"relativePath":"工具说明书/UnityModifier/多洛克小镇Mod/ModSettingManagerForDolocTown.readme.md","filePath":"工具说明书/UnityModifier/多洛克小镇Mod/ModSettingManagerForDolocTown.readme.md"}'),l={name:"工具说明书/UnityModifier/多洛克小镇Mod/ModSettingManagerForDolocTown.readme.md"};function d(p,o,s,g,c,m){return n(),a("div",null,o[0]||(o[0]=[t('<h1 id="modsettingmanagerfordoloctown-readme" tabindex="-1">ModSettingManagerForDolocTown.readme <a class="header-anchor" href="#modsettingmanagerfordoloctown-readme" aria-label="Permalink to &quot;ModSettingManagerForDolocTown.readme&quot;">​</a></h1><p><strong>ModSettingManagerForDolocTown</strong> 是一款为《多洛可小镇》《DolocTown》设计的基于 BepInEx 的轻量级设置注入插件，支持 mod 开发者将配置项集成至游戏原生设置面板中。</p><p><img src="'+e+'" alt=""></p><h2 id="功能特性" tabindex="-1">功能特性 <a class="header-anchor" href="#功能特性" aria-label="Permalink to &quot;功能特性&quot;">​</a></h2><ul><li><p><strong>支持的设置类型</strong>：Toggle、Slider、Dropdown、KeyBindDropdown（按键绑定）</p></li><li><p><strong>基于 BepInEx.ConfigEntry</strong>：配置项独立存储，支持 ConfigurationManager 可视化兼容，不参与游戏本身的设置存储</p></li><li><p>**支持多个 mod 共用，支持 Section 分组</p></li><li><p><strong>提供友好的 API 调用方式 (Fluent 风格)</strong></p></li></ul><p><img src="'+i+'" alt=""></p><ul><li><strong>不保证没 BUG</strong></li></ul><p>通过网盘分享的文件：<br> 链接: <a href="https://pan.baidu.com/s/1FPWUoUrnm2HwJ5G1Be8zGg?pwd=yh39" target="_blank" rel="noreferrer">https://pan.baidu.com/s/1FPWUoUrnm2HwJ5G1Be8zGg?pwd=yh39</a> 提取码: yh39 复制这段内容后打开百度网盘手机 App，操作更方便哦</p><h2 id="一些碎碎语" tabindex="-1">一些碎碎语 <a class="header-anchor" href="#一些碎碎语" aria-label="Permalink to &quot;一些碎碎语&quot;">​</a></h2><ul><li><p>很喜欢这个游戏的风格，难得国内有这么精致的种田游戏，游戏创意工坊上线似乎还有一段时间，暂时先凑合着用，希望能撑到开放对应接口。</p></li><li><p>本来只是想简单写一个简单的钓鱼 Patch Mod，减轻我的腱鞘炎，写着写着就想试一下注入面板，又写着写着就抽象成一个独立插件，现在好啦，腱鞘炎更严重了。</p></li><li><p>游戏中的按键系统实在是太硬编码了，没有那个能力做组件级复刻，只能用 Dropdown 了</p></li><li><p>目前只实现了单向数据流，即游戏 UI -&gt; ConfigEntry -&gt; ConfigurationManager，由于游戏 UI 面板是行为驱动的，非数据驱动，强行实现双向数据响应很困难，因此现在 ConfigurationManager 中的行为没有办法让 UI 面板实时响应。</p></li></ul>',10)]))}const h=r(l,[["render",d]]);export{f as __pageData,h as default};
